// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/IHiBeatsInterfaces.sol";

/**
 * @title HiBeatsFactoryFixed
 * @dev Fixed Factory contract for creating and managing music NFTs with AI integration
 * Handles Suno AI callback processing, IPFS metadata management, and reward distribution
 */
contract HiBeatsFactoryFixed is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    IHiBeatsNFT public immutable hiBeatsNFT;
    IHiBeatsToken public immutable beatsToken;
    address public ipfsService;

    // Generation settings
    uint256 public generationFee = 0.001 ether; // STT
    uint256 public rewardAmount = 10 * 10**18; // 10 BEATS
    uint256 public dailyLimit = 3;

    // Generation tracking
    struct GenerationRequest {
        address user;
        string prompt;
        string style;
        bool instrumental;
        uint256 timestamp;
        bool completed;
        string metadataURI;
        uint256 tokenId;
    }

    mapping(uint256 => GenerationRequest) public generationRequests;
    mapping(address => uint256[]) public userRequests;
    mapping(address => uint256) public dailyGenerations;
    mapping(address => uint256) public lastGenerationDay;
    
    // Callback authorization
    mapping(address => bool) public authorizedCallbackSenders;

    uint256 public nextRequestId = 1;

    // Events
    event MusicGenerationRequested(
        uint256 indexed requestId,
        address indexed user,
        string prompt,
        string style,
        bool instrumental
    );

    event MusicGenerationCompleted(
        uint256 indexed requestId,
        address indexed user,
        uint256 indexed tokenId,
        string metadataURI
    );

    event GenerationFailed(
        uint256 indexed requestId,
        address indexed user,
        string reason
    );

    event RewardDistributed(
        address indexed user,
        uint256 amount
    );

    modifier onlyAuthorizedCallback() {
        require(authorizedCallbackSenders[msg.sender], "Unauthorized callback sender");
        _;
    }

    modifier withinDailyLimit() {
        uint256 currentDay = block.timestamp / 1 days;
        if (lastGenerationDay[msg.sender] != currentDay) {
            dailyGenerations[msg.sender] = 0;
            lastGenerationDay[msg.sender] = currentDay;
        }
        require(dailyGenerations[msg.sender] < dailyLimit, "Daily generation limit exceeded");
        _;
    }

    constructor(
        address _hiBeatsNFT,
        address _beatsToken,
        address _ipfsService
    ) Ownable(msg.sender) {
        hiBeatsNFT = IHiBeatsNFT(_hiBeatsNFT);
        beatsToken = IHiBeatsToken(_beatsToken);
        ipfsService = _ipfsService;
        
        // Authorize owner for callbacks initially
        authorizedCallbackSenders[msg.sender] = true;
    }

    /**
     * @dev Request music generation through Suno AI
     */
    function requestMusicGeneration(
        string memory prompt,
        string memory style,
        bool instrumental
    ) external payable nonReentrant whenNotPaused withinDailyLimit {
        require(msg.value >= generationFee, "Insufficient fee");
        require(bytes(prompt).length > 0, "Prompt cannot be empty");

        uint256 requestId = nextRequestId++;
        
        generationRequests[requestId] = GenerationRequest({
            user: msg.sender,
            prompt: prompt,
            style: style,
            instrumental: instrumental,
            timestamp: block.timestamp,
            completed: false,
            metadataURI: "",
            tokenId: 0
        });

        userRequests[msg.sender].push(requestId);
        dailyGenerations[msg.sender]++;

        emit MusicGenerationRequested(requestId, msg.sender, prompt, style, instrumental);

        // Refund excess payment
        if (msg.value > generationFee) {
            payable(msg.sender).transfer(msg.value - generationFee);
        }
    }

    /**
     * @dev Complete music generation (called by authorized callback)
     */
    function completeMusicGeneration(
        uint256 requestId,
        string memory audioUrl,
        string memory imageUrl,
        string memory title,
        string memory artist
    ) external onlyAuthorizedCallback {
        GenerationRequest storage request = generationRequests[requestId];
        require(!request.completed, "Request already completed");
        require(request.user != address(0), "Invalid request");

        // Create metadata
        string memory metadataURI = _createMetadata(audioUrl, imageUrl, title, artist, request);
        
        // Mint NFT
        uint256 tokenId = hiBeatsNFT.mintTrack(
            request.user,
            string(abi.encodePacked("FACTORY_", requestId)),  // sunoId
            metadataURI,                                      // metadataURI
            1000                                              // royaltyPercentage (10%)
        );

        // Update request
        request.completed = true;
        request.metadataURI = metadataURI;
        request.tokenId = tokenId;

        // Distribute reward
        _distributeReward(request.user);

        emit MusicGenerationCompleted(requestId, request.user, tokenId, metadataURI);
    }

    /**
     * @dev Mark generation as failed
     */
    function markGenerationFailed(
        uint256 requestId,
        string memory reason
    ) external onlyAuthorizedCallback {
        GenerationRequest storage request = generationRequests[requestId];
        require(!request.completed, "Request already completed");
        require(request.user != address(0), "Invalid request");

        // Refund generation fee
        payable(request.user).transfer(generationFee);
        
        // Decrease daily counter
        uint256 currentDay = block.timestamp / 1 days;
        if (lastGenerationDay[request.user] == currentDay) {
            dailyGenerations[request.user]--;
        }

        // Mark as completed to prevent reprocessing
        request.completed = true;

        emit GenerationFailed(requestId, request.user, reason);
    }

    /**
     * @dev Create metadata URI for generated music
     */
    function _createMetadata(
        string memory audioUrl,
        string memory imageUrl,
        string memory title,
        string memory artist,
        GenerationRequest memory request
    ) internal pure returns (string memory) {
        // In real implementation, this would create proper JSON metadata
        // For now, return a simple concatenated string
        return string(abi.encodePacked(
            "ipfs://metadata/",
            title, "/",
            artist, "/",
            audioUrl, "/",
            imageUrl
        ));
    }

    /**
     * @dev Distribute BEATS reward to user
     */
    function _distributeReward(address user) internal {
        if (rewardAmount > 0) {
            beatsToken.mintForRewards(user, rewardAmount);
            emit RewardDistributed(user, rewardAmount);
        }
    }

    /**
     * @dev Setup function to authorize the factory after deployment
     */
    function setupAuthorizations() external onlyOwner {
        // This should be called after deployment to setup permissions
        hiBeatsNFT.setAuthorizedCreator(address(this), true);
    }

    // Admin functions
    function setAuthorizedCallbackSender(address sender, bool authorized) external onlyOwner {
        authorizedCallbackSenders[sender] = authorized;
    }

    function setGenerationFee(uint256 newFee) external onlyOwner {
        generationFee = newFee;
    }

    function setRewardAmount(uint256 newAmount) external onlyOwner {
        rewardAmount = newAmount;
    }

    function setDailyLimit(uint256 newLimit) external onlyOwner {
        dailyLimit = newLimit;
    }

    function setIpfsService(address newService) external onlyOwner {
        ipfsService = newService;
    }

    // View functions
    function getUserRequests(address user) external view returns (uint256[] memory) {
        return userRequests[user];
    }

    function getGenerationRequest(uint256 requestId) external view returns (GenerationRequest memory) {
        return generationRequests[requestId];
    }

    function getDailyGenerationsLeft(address user) external view returns (uint256) {
        uint256 currentDay = block.timestamp / 1 days;
        if (lastGenerationDay[user] != currentDay) {
            return dailyLimit;
        }
        return dailyLimit - dailyGenerations[user];
    }

    // Emergency functions
    function withdrawFees() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }
}

