pragma solidity ^0.8.20;

interface IHiBeatsNFT {
    struct TrackInfo {
        string sunoId;
        string genre;
        uint256 duration;
        address creator;
        uint256 createdAt;
        string modelUsed;
        bool isRemixable;
        uint256 royaltyRate;
    }
    
    function getTrackInfo(uint256 tokenId) external view returns (TrackInfo memory);
    function ownerOf(uint256 tokenId) external view returns (address);
    function exists(uint256 tokenId) external view returns (bool);
    function setApprovalForAll(address operator, bool approved) external;
    function transferFrom(address from, address to, uint256 tokenId) external;
    function mintTrack(address to, string memory sunoId, string memory metadataURI, uint256 royaltyPercentage) external returns (uint256);
    function addMinter(address minter) external;
    function mintingFee() external view returns (uint256);
    function getUserLibrary(address user) external view returns (uint256[] memory);
    function totalSupply() external view returns (uint256);
    function setAuthorizedCreator(address creator, bool authorized) external;
    function getAllTracks() external view returns (uint256[] memory);
    function batchMintTracks(
        address to,
        string[] memory tokenURIs,
        string[] memory sunoIds,
        string[] memory genres,
        uint256[] memory durations,
        string[] memory modelsUsed,
        bool[] memory isRemixable,
        uint256[] memory royaltyRates
    ) external payable;
}

interface IHiBeatsToken {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function safeTransfer(address to, uint256 amount) external;
    function safeTransferFrom(address from, address to, uint256 amount) external;
    function balanceOf(address account) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function totalSupply() external view returns (uint256);
    function mint(address to, uint256 amount) external;
    function addMinter(address minter) external;
    function mintForRoyalties(address to, uint256 amount) external;
    function mintForRewards(address to, uint256 amount) external;
}

interface IHiBeatsProfile {
    function hasProfile(address user) external view returns (bool);
    function checkFollowing(address user1, address user2) external view returns (bool);
    function getUserSocialScore(address user) external view returns (uint256);
    function incrementCreatorStats(address user, string memory statType) external;
    function incrementUserStats(address user, string memory statType) external;
    function updateSocialScore(address user, int256 change) external;
    function addAuthorizedCaller(address caller) external;
    function updateStats(address user, uint256 trackCount, uint256 totalPlays, uint256 totalEarnings) external;
    function profiles(address user) external view returns (
        string memory username,
        string memory displayName,
        string memory bio,
        string memory avatar,
        string memory coverImage,
        string memory website,
        bool isVerified,
        bool isActive,
        uint256 createdAt,
        uint256 followerCount,
        uint256 followingCount,
        uint256 trackCount,
        uint256 totalPlays,
        uint256 totalEarnings
    );
    function getProfile(address user) external view returns (
        string memory username,
        string memory displayName,
        string memory bio,
        string memory avatar,
        string memory coverImage,
        string memory website,
        bool isVerified,
        bool isActive,
        uint256 createdAt,
        uint256 followerCount,
        uint256 followingCount,
        uint256 trackCount,
        uint256 totalPlays,
        uint256 totalEarnings
    );
    function getFollowStats(address user) external view returns (
        uint256 followerCount,
        uint256 followingCount,
        address[] memory followersList,
        address[] memory followingList
    );
}

interface IHiBeatsRoyalties {
    function addRevenue(uint256 amount, uint256 source, uint256 tokenId) external;
    function distributeRoyalties(uint256 tokenId, uint256 salePrice, bool isBeatsToken) external;
}

interface IHiBeatsMarketplace {
    function getRecentTrades(uint256 limit) external view returns (address[] memory, uint256[] memory, uint256[] memory);
}

interface IHiBeatsStaking {
    function getTotalStaked(address user) external view returns (uint256);
}

interface IHiBeatsAnalytics {
    function recordEvent(address user, string memory eventType, uint256 tokenId, uint256 value, bytes32 metadata) external;
    function recordTransaction(address buyer, address seller, uint256 tokenId, uint256 price, bool isBeatsToken) external;
    function addAuthorizedCaller(address caller) external;
}

interface IHiBeatsDiscovery {
    function addNewTrack(uint256 tokenId, string memory genre, string[] memory tags, address creator) external;
    function updateTrackActivity(uint256 tokenId, string memory activityType, uint256 value) external;
    function updateUserActivity(address user, string memory action, uint256 tokenId) external;
    function addAuthorizedCaller(address caller) external;
}

interface IHiBeatsPlaylist {
    function getUserLikedTracks(address user) external view returns (uint256[] memory);
    function getUserRecentlyPlayed(address user, uint256 limit) external view returns (uint256[] memory);
    function getUserMostPlayedTracks(address user, uint256 limit) external view returns (uint256[] memory, uint256[] memory);
    function getTrackAnalytics(uint256 trackId) external view returns (uint256, uint256, uint256);
}

